import React, { Component } from "React";

class Form extends Component {
    constructor(props) {
        super(props);
        this.initialState = {
            name: '',
            job: ''
        }
        this.state = this.initialState;
    }

    handleChange = event => {
        const {name, value} = event;
        this.setState = {
            [name] : value
        };
    }

    render() {
        return (
            <form>
                <label>Name</label>
                <input
                    type="text"
                    name="name"
                    value={name}
                    onChange={this.handleChange} />
                <label>Job</label>
                <input
                    type="text"
                    name="job"
                    value={job}
                    onChange={this.handleChange} />
            </form>
        )
    }
}

export default Form;
