import React, { Component } from "react";

class Form extends Component {
    constructor(props) {
        super(props);
        this.initialState = {
            name: '',
            job: ''
        }
        this.state = this.initialState;
    }

    handleChange = e => {
        const { name, value } = e.target;
        this.setState({
            [name] : value
        });
        // console.log(this.state)
    }

    submit = e => {
        console.log(`Form has been submitted ${this.state.name}`);
        e.preventDefault();
        const character = {
            name: this.state.name,
            job: this.state.job
        }
        const characters = this.state.characters.push(character);
        this.setState({
            characters: characters
        })
    }

    render() {
        return (
            <form onSubmit={this.submit}>
                <label>Name</label>
                <input
                    type="text"
                    name="name"
                    onChange={this.handleChange} />
                <label>Job</label>
                <input
                    type="text"
                    name="job"
                    onChange={this.handleChange} />
                    <button>Submit</button>
            </form>
        )
    }
}

export default Form;
